xpath.script:
                    document.addEventListener("DOMContentLoaded", function () {
                    // Get all menu items
                    let menuItems = document.querySelectorAll(".list-group-item");

                    menuItems.forEach(item => {
                    item.addEventListener("click", function () {
                    // Remove 'active' class from all items
                    menuItems.forEach(el => el.classList.remove("active"));

                    // Add 'active' class to the clicked item
                    this.classList.add("active");
                    });
                    });
                    });


                    document.addEventListener("DOMContentLoaded", function() {
                    // Get all sidebar links
                    const links = document.querySelectorAll(".list-group-item a");

                    // Hide all sections initially
                    document.querySelectorAll(".section").forEach((section, index) => {
                    if (index === 0) {
                    section.style.display = "block"; // Show the first section
                    } else {
                    section.style.display = "none"; // Hide others
                    }
                    });

                    links.forEach(link => {
                    link.addEventListener("click", function(event) {
                    event.preventDefault(); // Prevent default anchor behavior

                    let sectionId = this.getAttribute("data-section");

                    // Hide all sections
                    document.querySelectorAll(".section").forEach(section => {
                    section.style.display = "none";
                    });

                    // Show the clicked section
                    let activeSection = document.getElementById(sectionId);
                    if (activeSection) {
                    activeSection.style.display = "block";
                    }
                    });
                    });
                    });

                    function logoutUser() {
                    window.location.href = "/web/session/logout";
                    }

                    document.getElementById("fileInput").addEventListener("change", function(event) {
                    let previewContainer = document.getElementById("gallery-preview");
                    previewContainer.innerHTML = ""; // Clear previous previews

                    for (let file of event.target.files) {
                    if (file.type.startsWith("image/")) {
                    let reader = new FileReader();
                    let fileSize = (file.size / (1024 * 1024)).toFixed(1) + " MB"; // Convert size to MB

                    let imageCard = document.createElement("div");
                    imageCard.classList.add("image-card");

                    reader.onload = function(e) {
                    imageCard.innerHTML = `
                    
                    ${fileSize}




                    
                    `;
                    previewContainer.appendChild(imageCard);

                    // Simulate progress animation
                    setTimeout(() => {
                    imageCard.querySelector(".progress-bar-fill").style.width = "100%";
                    }, 300);
                    };

                    reader.readAsDataURL(file);
                    }
                    }
                    });

$(document).ready(function () {
$('form').submit(function (event) {
alert("Your property has been submitted successfully!");
});
});
